mapper.select:
SELECT ID,MyDate,SX,${DataType}  FROM ${TableName} WHERE  MyDate = #{DateString} AND purpose=#{Purpose} AND level=#{Level}


        AND ID=#{ID}


        AND ${DataType} IS NOT NULL AND ${DataType} >-99990 AND ID=#{ID}

SELECT ID,MyDate,SX,${DataType}  FROM ${TableName} WHERE  MyDate = #{DateString} AND purpose=#{Purpose} AND level=#{Level}


        AND ID IN ${ID}


        AND ${DataType} IS NOT NULL AND ${DataType} >-99990 AND ID IN ${ID}

SELECT ID,MyDate,SX,${DataType}  FROM ${TableName} WHERE  MyDate = #{DateString} AND purpose=#{Purpose} AND level=#{Level} AND SX=#{YbSx}


        AND ID IN ${ID}


        AND ${DataType} IS NOT NULL AND ${DataType} >-99990 AND ID IN ${ID}

SELECT ID,MyDate,SX,${DataType}  FROM ${TableName} WHERE  MyDate = #{DateString}  AND SX=#{YbSx}


        AND ID IN ${ID}


        AND ${DataType} IS NOT NULL AND ${DataType} >-99990 AND ID IN ${ID}

SELECT ID,MyDate,SX,${DataType}  FROM ${TableName} WHERE  MyDate = #{DateString}


        AND ID IN ${ID}


        AND ${DataType} IS NOT NULL AND ${DataType} >-99990 AND ID IN ${ID}
